CREATE OR REPLACE PROCEDURE desfazer_desconto_protocolo ( nr_seq_protocolo_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
C01 CURSOR FOR 
	SELECT	a.nr_interno_conta, 
		a.nr_atendimento, 
		b.nr_sequencia 
	from	conta_paciente a, 
		conta_paciente_desconto b 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	a.nr_seq_protocolo	= nr_seq_protocolo_p;

nr_interno_conta_w	bigint;
nr_atendimento_w	bigint;
nr_seq_desconto_w	bigint;

BEGIN
 
open C01;
loop 
fetch C01 into 
	nr_interno_conta_w, 
	nr_atendimento_w, 
	nr_seq_desconto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL calcular_conpaci_desconto(nr_seq_desconto_w, 'E', nm_usuario_p);
	CALL recalcular_conta_paciente(nr_interno_conta_w, nm_usuario_p);
	end;
end loop;
close C01;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
