CREATE OR REPLACE PROCEDURE desfazer_encerramento_contrato ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_historico_w			bigint;
ds_historico_w			varchar(4000);
ds_titulo_w			varchar(255);


BEGIN

update	contrato
set	ie_estagio 	= 'C',
	dt_encerramento	 = NULL,
	nm_usuario_encer	 = NULL,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p,
	ie_situacao	= 'A'
where	nr_sequencia	= nr_sequencia_p;

ds_titulo_w :=	wheb_mensagem_pck.get_Texto(311434);/* 'Desfeito encerramento do contrato';*/
ds_historico_w :=	wheb_mensagem_pck.get_Texto(311435, 'NM_USUARIO_P='|| NM_USUARIO_P); /*'O usu√°rio ' || nm_usuario_p || ' desfez o encerramento do contrato.';*/
select	nextval('contrato_historico_seq')
into STRICT	nr_seq_historico_w
;

insert into contrato_historico(
	nr_sequencia,
	nr_seq_contrato,
	dt_historico,
	ds_historico,
	dt_atualizacao,
	nm_usuario,
	ds_titulo,
	dt_liberacao,
	nm_usuario_lib,
	ie_efetivado) values (
		nr_seq_historico_w,
		nr_sequencia_p,
		clock_timestamp(),
		ds_historico_w,
		clock_timestamp(),
		nm_usuario_p,
		ds_titulo_w,
		clock_timestamp(),
		nm_usuario_p,
		'N');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
