SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desfazer_envelopamento ( NR_SEQ_ENVELOPE_P bigint, nm_usuario_p text) AS $body$
DECLARE


c01 CURSOR FOR
SELECT nr_prescricao,
       nr_seq_prescr
from envelope_laudo_item
where nr_seq_envelope = NR_SEQ_ENVELOPE_P;

nr_prescricao_w      envelope_laudo_item.nr_prescricao%Type;
nr_seq_prescricao_w  envelope_laudo_item.nr_seq_prescr%Type;



BEGIN

  open C01;
  loop
  fetch C01 into
    nr_prescricao_w,
    nr_seq_prescricao_w;
  EXIT WHEN NOT FOUND; /* apply on C01 */
    begin

    update laudo_paciente
    set dt_envelopado  = NULL,
        dt_conferencia  = NULL,
        nm_usuario = nm_usuario_p,
        dt_atualizacao = clock_timestamp()
    where nr_prescricao = nr_prescricao_w
    and nr_seq_prescricao = nr_seq_prescricao_w;

    update prescr_procedimento
    set ie_status_execucao = '40'
    where nr_prescricao = nr_prescricao_w
    and nr_sequencia = nr_seq_prescricao_w;

    end;
  end loop;
  close C01;

delete from registro_entrega_hist where nr_seq_envelope = nr_seq_envelope_p and ie_etapa_hist = 'EI';

delete from ENVELOPE_LAUDO_ITEM where nr_seq_envelope = nr_seq_envelope_p;

delete from ENVELOPE_LAUDO where nr_sequencia = nr_seq_envelope_p;

commit;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
