SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desfazer_enviar_prot_doc ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_atual_w				protocolo_documento.cd_pessoa_destino%type;
cd_pessoa_w				protocolo_documento.cd_pessoa_destino%type;
dt_rec_destino_w		protocolo_documento.dt_rec_destino%type;
ie_status_prot_w		varchar(1);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select	max(dt_rec_destino),
		max(cd_pessoa_destino)
	into STRICT	dt_rec_destino_w,
		cd_atual_w
	from	protocolo_documento
	where	nr_sequencia = nr_sequencia_p;

	select	substr(obter_dados_usuario_opcao(nm_usuario_p,'C'),1,10)
	into STRICT	cd_pessoa_w
	;

	if (dt_rec_destino_w IS NOT NULL AND dt_rec_destino_w::text <> '') then

		ie_status_prot_w := 'T';

		if (cd_atual_w = cd_pessoa_w) then
			ie_status_prot_w := 'R';
		end if;
	end if;

	update	protocolo_documento
	set    	dt_envio 			 = NULL,
		nm_usuario 		= nm_usuario_p,
		nm_usuario_envio 		 = NULL,
		ie_status_protocolo = ie_status_prot_w
	where  nr_sequencia 		= nr_sequencia_p;
	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
