SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desfazer_escritural_darf ( nr_seq_darf_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_remover_titulo_w	varchar(1);
nr_seq_escritural_w	bigint;
ds_arquivo_w		varchar(255);
ie_lib_banco_escrit_w	varchar(1);
dt_liberacao_w		timestamp;
ie_remessa_retorno_w	varchar(1);
dt_baixa_w		timestamp;


BEGIN

ie_remover_titulo_w := obter_param_usuario(857, 38, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_remover_titulo_w);

select	max(a.nr_seq_escritural)
into STRICT	nr_seq_escritural_w
from	darf a
where	a.nr_sequencia	= nr_seq_darf_p;

select	max(a.ds_arquivo),
	max(a.dt_liberacao),
	coalesce(max(a.ie_remessa_retorno),'R'),
	max(a.dt_baixa)
into STRICT	ds_arquivo_w,
	dt_liberacao_w,
	ie_remessa_retorno_w,
	dt_baixa_w
from	banco_escritural a
where	a.nr_sequencia	= nr_seq_escritural_w;

if (dt_baixa_w IS NOT NULL AND dt_baixa_w::text <> '') then

	/* O pagamento escritural já foi baixado. Não pode ser desfeito! */

	CALL wheb_mensagem_pck.exibir_mensagem_abort(228262);

end if;

if (coalesce(ie_remover_titulo_w,'S') = 'N') and (ie_remessa_retorno_w = 'R') and (ds_arquivo_w IS NOT NULL AND ds_arquivo_w::text <> '') then

	/* O pagamento escritural já teve arquivo remessa gerado. Não pode ser desfeito! Parâmetro [38] */

	CALL wheb_mensagem_pck.exibir_mensagem_abort(228256);

end if;

select	coalesce(max(a.ie_lib_banco_escrit),'N')
into STRICT	ie_lib_banco_escrit_w
from	parametros_contas_pagar a
where	a.cd_estabelecimento	= cd_estabelecimento_p;

if (ie_lib_banco_escrit_w	= 'S') and (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then

	/* Este pagamento já foi liberado, não é possível retirar os títulos! */

	CALL wheb_mensagem_pck.exibir_mensagem_abort(228261);

end if;

update	darf
set	nr_seq_escritural	 = NULL,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_darf_p;

delete	from titulo_pagar_escrit
where	nr_seq_escrit	= nr_seq_escritural_w;

delete	from banco_escritural
where	nr_sequencia	= nr_seq_escritural_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
