CREATE OR REPLACE PROCEDURE desfazer_finalizar_problema ( nr_sequencia_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE



ie_status_w varchar(1);

BEGIN
	select	max(ie_status)
	into STRICT	ie_status_w
	from	lista_problema_status
	where	nr_seq_problema = nr_sequencia_p;

	update	lista_problema_pac
	set	ds_observacao = ds_observacao_p,
		ie_status = ie_status_w,
		dt_fim  = NULL,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where nr_sequencia = nr_sequencia_p;

commit;

end;
