CREATE OR REPLACE PROCEDURE desfazer_geracao_lote_recurso (nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_item_w	smallint;


BEGIN

update	conta_paciente_ret_hist
set	nr_seq_lote_recurso  = NULL,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_seq_lote_recurso = nr_seq_lote_p;

select	count(*)
into STRICT	qt_item_w
from	conta_paciente_ret_hist
where	nr_seq_lote_recurso = nr_seq_lote_p;

if (qt_item_w = 0) then

	update lote_audit_recurso
	set	dt_geracao  = NULL,
		nm_usuario = nm_usuario_p,
		dt_envio  = NULL,
		dt_prev_retorno  = NULL
	where	nr_sequencia = nr_seq_lote_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
