SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desfazer_hist_audit_retorno ( nr_seq_retorno_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_ret_item_w		bigint;
nr_seq_conpaci_ret_hist_w	bigint;

c01 CURSOR FOR 
	SELECT	distinct a.nr_seq_ret_item 
	from	motivo_glosa c, 
		convenio_retorno_item b, 
		convenio_retorno_glosa a 
	where	a.nr_seq_ret_item	= b.nr_sequencia 
	and	a.cd_motivo_glosa	= c.cd_motivo_glosa 
	and	coalesce(a.ie_acao_glosa,c.ie_acao_glosa) = 'R' 
	and	b.nr_seq_retorno	= nr_seq_retorno_p;

c02 CURSOR FOR 
	SELECT	a.nr_seq_conpaci_ret_hist 
	from	motivo_glosa b, 
		convenio_retorno_glosa a 
	where	a.cd_motivo_glosa	= b.cd_motivo_glosa 
	and	coalesce(a.ie_acao_glosa,b.ie_acao_glosa) = 'R' 
	and	(a.nr_seq_conpaci_ret_hist IS NOT NULL AND a.nr_seq_conpaci_ret_hist::text <> '') 
	and	a.nr_seq_ret_item	= nr_seq_ret_item_w;


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_seq_ret_item_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	 
	open c02;
	loop 
	fetch c02 into 
		nr_seq_conpaci_ret_hist_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		CALL desfazer_historico_auditoria(nr_seq_conpaci_ret_hist_w, nm_usuario_p);
	end loop;
	close c02;
	 
end loop;
close c01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
