CREATE OR REPLACE PROCEDURE desfazer_inicio_administracao ( nr_seq_atendimento_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

 
ie_permite_desfazer_inicio_w	varchar(1);
ie_paciente_atend_medic_w	varchar(1);
nr_atendimento_w			prescr_medica.nr_atendimento%type;
nr_prescricao_w				prescr_medica.nr_prescricao%type;


BEGIN 
 
/* Quimioterapia - Parâmetro [185] - Permite desfazer início da administração com medicamento checado */
 
ie_permite_desfazer_inicio_w := obter_param_usuario(3130, 185, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_permite_desfazer_inicio_w);
 
if (ie_permite_desfazer_inicio_w = 'S') then 
	begin 
	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END  
	into STRICT	ie_paciente_atend_medic_w 
	from	paciente_atend_medic 
	where	nr_seq_atendimento	= nr_seq_atendimento_p 
	and	ie_checado	= 'S';
 
	if (ie_paciente_atend_medic_w = 'S') then 
		ds_erro_p	:= substr(obter_texto_tasy(59936, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	end if;
	end;
end if;
 
if (coalesce(ds_erro_p::text, '') = '') then 
	CALL atualiza_data_campo_pac_atend('DT_INICIO_ADM', nr_seq_atendimento_p, 'D', nm_usuario_p);
	 
	select	max(a.nr_prescricao), 
			max(b.nr_atendimento) 
	into STRICT	nr_prescricao_w, 
			nr_atendimento_w 
	from	prescr_material a, 
			prescr_medica b 
	where	a.nr_prescricao = b.nr_prescricao 
	and		a.nr_seq_atendimento = nr_seq_atendimento_p;
	 
	if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then 
		begin 
		CALL adm_medic_adep_quimio(nr_seq_atendimento_p, nr_atendimento_w, clock_timestamp(), nr_prescricao_w, nm_usuario_p, cd_estabelecimento_p, 'R', null);	
		end;
	end if;
	 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
