CREATE OR REPLACE PROCEDURE desfazer_inicio_etapa ( nr_seq_etapa_p text, dt_tipo_p text, nm_usuario_p text) AS $body$
DECLARE

/*
CC - DT_CANCELAMENTO
CF - DT_CONFIRMACAO
IN - DT_INICIO_REAL
FN - DT_FIM_ETAPA
*/
BEGIN

if (nr_seq_etapa_p IS NOT NULL AND nr_seq_etapa_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	if (dt_tipo_p = 'IN') then
		begin
		update 	checkup_etapa set
			dt_inicio_real 	    = NULL,
			dt_confirmacao 	    = NULL,
			NM_USUARIO_CONF     = NULL,
			nm_usuario 	   =	nm_usuario_p
			where nr_sequencia = 	nr_seq_etapa_p;
			commit;
		end;
	elsif (dt_tipo_p = 'CC') then
		begin
		update 	checkup_etapa set
			dt_cancelamento     = NULL,
			nm_usuario_cancel   = NULL,
			nm_usuario 	   =	nm_usuario_p
			where nr_sequencia = 	nr_seq_etapa_p;
			commit;
		end;
	elsif (dt_tipo_p = 'FN') then
		begin
		update 	checkup_etapa set
			dt_fim_etapa 	    = NULL,
			nm_usuario 	   =	nm_usuario_p
			where nr_sequencia = 	nr_seq_etapa_p;
			commit;
		end;
	end if;
	end;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
