SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desfazer_parc_emprest_financ (nr_seq_contrato_p bigint) AS $body$
DECLARE


qt_parc_exist_w		bigint;
qt_parc_titulo_w	bigint;


BEGIN

-- Verifica se existem parcelas geradas para o contrato
select	count(*)
into STRICT	qt_parc_exist_w
from	emprest_financ_parc
where	nr_seq_contrato = nr_seq_contrato_p;
if (coalesce(qt_parc_exist_w,0) > 0) then
	-- Verifica se as parcelas possuem título gerado
	select	count(*)
	into STRICT	qt_parc_titulo_w
	from	emprest_financ_parc
	where	nr_seq_contrato = nr_seq_contrato_p
	and	(nr_titulo IS NOT NULL AND nr_titulo::text <> '');
	if (coalesce(qt_parc_titulo_w,0) > 0) then
		-- Já existem parcelas geradas com título vinculado. Não é possível desfazer as parcelas.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(368810);
	else
		delete	from emprest_financ_parc
		where	nr_seq_contrato = nr_seq_contrato_p;
		commit;
	end if;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE desfazer_parc_emprest_financ (nr_seq_contrato_p bigint) FROM PUBLIC;

