CREATE OR REPLACE PROCEDURE desfazer_proced_conta_apac ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_interno_conta_w	bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
nr_sequencia_w		bigint;

 
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		cd_procedimento, 
		ie_origem_proced 
	from	sus_apac_proc 
	where	nr_seq_apac	= nr_sequencia_p;


BEGIN 
 
/* Tirar a conta do protocolo e passar para provis√≥rio */
 
 
select	nr_interno_conta 
into STRICT	nr_interno_conta_w 
from	sus_apac_movto 
where	nr_sequencia	= nr_sequencia_p;
 
update	conta_paciente 
set	nr_seq_protocolo	 = NULL, 
	nr_protocolo		= '0', 
	ie_status_acerto	= 1 
where	nr_interno_conta = nr_interno_conta_w;
 
/* Ecluir os procedimentos da conta do paciente e da APAC */
 
 
open C01;
loop 
	fetch C01 into 	 
		nr_sequencia_w, 
		cd_procedimento_w, 
		ie_origem_proced_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	update	sus_apac_proc 
	set	nr_seq_propaci	 = NULL 
	where	nr_sequencia		= nr_sequencia_w;	
 
/*	delete	procedimento_paciente a 
	where	a.nr_interno_conta	= nr_interno_conta_w 
	and	a.cd_procedimento	= cd_procedimento_w 
	and	a.ie_origem_proced	= ie_origem_proced_w 
	and	not exists (	select	1 
				from	sus_apac_proc b 
				where	b.nr_seq_apac	= nr_sequencia_p 
				and	b.nr_seq_propaci= a.nr_sequencia); 
*/
 
	end;
end loop;
close C01;
 
/* Desvincular a APAC da conta */
 
 
update	sus_apac_movto 
set	nr_interno_conta  = NULL 
where	nr_sequencia	 = nr_sequencia_p;
 
commit;
 
END;
$body$
