SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desfazer_processo_adep (nr_processo_p bigint, ie_excluir_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
ie_estorna_conta_w		varchar(1);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	material_atend_paciente
	where	nr_seq_processo = nr_processo_p;


BEGIN

ie_estorna_conta_w := Obter_Param_Usuario(3112, 53, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_estorna_conta_w);

if (nr_processo_p IS NOT NULL AND nr_processo_p::text <> '') then
	if (ie_estorna_conta_w = 'S') then
		open C01;
		loop
		fetch C01 into
		nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			begin
				CALL voltar_item_pendente_prescr(nr_sequencia_w,nm_usuario_p);
				exception
				when others then
					update	material_atend_paciente
					set    	nr_seq_processo  = NULL
					where  	nr_seq_processo = nr_processo_p;
			end;

			end;
		end loop;
		close C01;
		update	material_atend_paciente
		set    	nr_seq_processo  = NULL
		where  	nr_seq_processo = nr_processo_p;
	end if;

	update	adep_processo
	set	nr_seq_processo	 = NULL
	where	nr_seq_processo	= nr_processo_p;

	update	prescr_mat_hor
	set    	nr_seq_processo 	 = NULL,
		nr_seq_etiqueta	 = NULL,
		nr_seq_etiqueta_reaprov  = NULL
	where  	nr_seq_processo 	= nr_processo_p;

	update	material_atend_paciente
	set    	nr_seq_processo  = NULL
	where  	nr_seq_processo = nr_processo_p;

	delete
	from	adep_processo_item
	where 	nr_seq_processo = nr_processo_p;

	delete
	from	adep_processo_frac
	where	nr_seq_processo = nr_processo_p;

	delete
	from	adep_processo_area
	where	nr_seq_processo = nr_processo_p;

	/*
	delete
	from	adep_processo_item
	where 	nr_seq_processo = nr_processo_p;

	delete
	from	adep_processo_item_adic
	where 	nr_seq_processo = nr_processo_p;
	*/
	delete
	from 	adep_processo_controle
	where 	nr_Seq_processo = nr_processo_p;

	delete
	from 	adep_processo
	where 	nr_sequencia = nr_processo_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
