CREATE OR REPLACE PROCEDURE desfazer_rateio_nota_credito ( nr_seq_retorno_p bigint) AS $body$
BEGIN

if (nr_seq_retorno_p IS NOT NULL AND nr_seq_retorno_p::text <> '') then
update	convenio_retorno_item
set	vl_pago           = coalesce(vl_pago,0) + coalesce(vl_nota_credito,0),
	vl_nota_credito   = 0
where	nr_seq_retorno    = nr_seq_retorno_p;
end if;

commit;

end;
