CREATE OR REPLACE PROCEDURE desfazer_reaproveitamento ( nr_seq_ordem_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
BEGIN

delete
from 	can_reaproveitamento
where 	nr_seq_ordem_origem = nr_seq_ordem_p;

delete
from 	can_ordem_prod_mat
where 	nr_seq_ordem_origem = nr_seq_ordem_p
and	coalesce(ie_reaproveitado,'S') <> 'N'
and 	nr_sequencia <> nr_sequencia_p;

commit;

end;
