SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desfazer_repasse_item_glosa (nr_seq_ret_glosa_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_repasse_terceiro_w	bigint;


BEGIN

select	max(b.nr_repasse_terceiro)
into STRICT	nr_repasse_terceiro_w
from	repasse_terceiro b,
	repasse_terceiro_item a
where	b.ie_status		= 'F'
and	a.nr_repasse_terceiro	= b.nr_repasse_terceiro
and	a.nr_seq_ret_glosa		= nr_seq_ret_glosa_p;

if (coalesce(nr_repasse_terceiro_w::text, '') = '') then
	delete 	from repasse_terceiro_item
	where	nr_seq_ret_glosa	= nr_seq_ret_glosa_p;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265467,'nr_repasse_terceiro= ' || nr_repasse_terceiro_w);
	--Mensagem: O repasse || nr_repasse_terceiro_w || já está fechado!
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
