CREATE OR REPLACE PROCEDURE desfazer_repasse_prod_plantao (nr_seq_prod_plantao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_item_w	bigint;
vl_total_w	double precision;
ds_ocorrencia_w	varchar(4000);


BEGIN

select	count(*),
	sum(a.vl_repasse)
into STRICT	qt_item_w,
	vl_total_w
from	repasse_producao_plantao a
where	a.nr_seq_prod_plantao	= nr_seq_prod_plantao_p;

delete	from repasse_producao_plantao
where	nr_seq_prod_plantao	= nr_seq_prod_plantao_p;

/*ds_ocorrencia_w	:=	'Desfeita simulação de repasse.' || chr(13) || chr(10) ||
			'Itens excluídos da simulação: ' || qt_item_w || chr(13) || chr(10) ||
			'Valor total da simulação: ' || to_char(vl_total_w,'999999990.00');*/
ds_ocorrencia_w	:=	wheb_mensagem_pck.get_texto(304458,'QT_ITEM_W='||qt_item_w||';VL_TOTAL_W='||to_char(vl_total_w,'999999990.00'));

CALL gerar_repasse_prod_plantao_log(	nr_seq_prod_plantao_p,
				'F',
				'S',
				ds_ocorrencia_w,
				nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
