SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desfazer_repasse_simulacao (nr_seq_prod_plantao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_item_repasse_w	bigint;
ds_ocorrencia_w		varchar(4000);


BEGIN

select	count(*)
into STRICT	qt_item_repasse_w
from	repasse_terceiro_item b,
	repasse_producao_plantao a
where	(b.nr_repasse_terceiro IS NOT NULL AND b.nr_repasse_terceiro::text <> '')
and	a.nr_sequencia		= b.nr_seq_repasse_prod
and	a.nr_seq_prod_plantao	= nr_seq_prod_plantao_p;

if (qt_item_repasse_w	> 0) then

	ds_ocorrencia_w	:=	substr(wheb_mensagem_pck.get_texto(304951),1,4000);

	CALL gerar_repasse_prod_plantao_log(	nr_seq_prod_plantao_p,
					'I',
					'R',
					ds_ocorrencia_w,
					nm_usuario_p);

end if;

if (coalesce(ds_ocorrencia_w::text, '') = '') then

	delete	from repasse_terceiro_item a
	where	exists (SELECT	1
		from	repasse_producao_plantao x
		where	x.nr_seq_prod_plantao	= nr_seq_prod_plantao_p
		and	x.nr_sequencia		= a.nr_seq_repasse_prod);

	ds_ocorrencia_w	:= substr(wheb_mensagem_pck.get_texto(304952),1,4000);

	CALL gerar_repasse_prod_plantao_log(	nr_seq_prod_plantao_p,
					'F',
					'R',
					ds_ocorrencia_w,
					nm_usuario_p);

	update	producao_medica_plantao
	set	dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_geracao_repasse	 = NULL
	where	nr_sequencia		= nr_seq_prod_plantao_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
