SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desfazer_reprov_medic_cih ( nr_prescricao_p bigint, nr_seq_material_p bigint) AS $body$
BEGIN
	begin
		if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then

			update	prescr_material
			set		ds_reprov_ccih  = NULL,
					dt_aprovacao_cih  = NULL,
					nr_seq_motivo_reprov  = NULL,
					ie_ciclo_reprov = 'N',
					qt_dias_liberado  = NULL
			where	nr_sequencia = nr_seq_material_p
			and		nr_prescricao = nr_prescricao_p;
			
			delete	FROM prescr_material_lib_cih
			where	nr_prescricao = nr_prescricao_p
			and		nr_seq_material = nr_seq_material_p
			and		ie_tipo_liberacao = 'R';

			delete	FROM rep_controle_atb
			where	nr_prescricao = nr_prescricao_p
			and		nr_seq_material = nr_seq_material_p;

		end if;

		if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then
			commit;
		end if;

	exception
	when others then
		CALL gerar_log_prescricao(nr_prescricao_p, nr_seq_material_p, null, null, 'M', substr('exception: ' || sqlerrm,1, 1800), wheb_usuario_pck.get_nm_usuario, 43041, 'N');
	end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
