CREATE OR REPLACE PROCEDURE desfazer_substituicao_mat_lp ( nr_lote_producao_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_prevista_w	double precision;


BEGIN

if	(nr_lote_producao_p > 0 AND nr_sequencia_p > 0) then

	select	max(qt_prevista)
	into STRICT	qt_prevista_w
	from	lote_producao
	where	nr_lote_producao = nr_lote_producao_p;

	update	lote_producao_comp
	set	cd_material		=	cd_material_subs,
		qt_prevista		=	qt_prevista_w * qt_unitaria_subs,
		qt_unitaria		=	qt_unitaria_subs,
		cd_material_subs	 = NULL,
		qt_unitaria_subs	 = NULL
	where	nr_lote_producao	=	nr_lote_producao_p
	and	nr_sequencia		=	nr_sequencia_p;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
