SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desfazer_susp_dieta_jejum ( nr_seq_jejum_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_prescricao_w		bigint;
nr_prescr_item_w	bigint;
nr_sequencia_w		bigint;
nr_seq_item_w		bigint;
nr_atendimento_w	bigint;
dt_inicio_w		timestamp;
dt_fim_w		timestamp;
nm_tabela_w		varchar(255);

c01 CURSOR FOR
SELECT	c.nr_sequencia,
	b.nr_sequencia,
	b.nr_prescricao,
	'PRESCR_MATERIAL'
from	prescr_medica a,
	prescr_material b,
	prescr_mat_hor c
where	a.nr_prescricao	= b.nr_prescricao
and	a.nr_prescricao	= c.nr_prescricao
and	b.nr_prescricao	= c.nr_prescricao
and	b.nr_sequencia	= c.nr_seq_material
and	a.nr_atendimento = nr_atendimento_w
and	c.nr_seq_jejum_susp = nr_Seq_jejum_p
and	c.dt_horario	between dt_inicio_w and dt_fim_w

union

select	c.nr_sequencia,
	b.nr_sequencia,
	b.nr_prescricao,
	'PRESCR_DIETA'
from	prescr_medica a,
	prescr_dieta b,
	prescr_dieta_hor c
where	a.nr_prescricao	= b.nr_prescricao
and	a.nr_prescricao	= c.nr_prescricao
and	b.nr_prescricao	= c.nr_prescricao
and	b.nr_sequencia	= c.nr_seq_dieta
and	a.nr_atendimento = nr_atendimento_w
and	c.nr_seq_jejum_susp = nr_Seq_jejum_p
and	c.dt_horario	between dt_inicio_w and dt_fim_w;


BEGIN

select	max(nr_prescricao),
	max(dt_inicio),
	max(dt_fim)
into STRICT	nr_prescricao_w,
	dt_inicio_w,
	dt_fim_w
from	rep_jejum
where	nr_sequencia	= nr_seq_jejum_p;

select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_w;

if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then

	open C01;
	loop
	fetch C01 into
		nr_sequencia_w,
		nr_seq_item_w,
		nr_prescr_item_w,
		nm_tabela_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (nm_tabela_w	= 'PRESCR_MATERIAL') then

			update	prescr_mat_hor
			set	nr_seq_jejum_susp	 = NULL,
				dt_suspensao		 = NULL,
				nm_usuario_susp		 = NULL,
				ie_suspenso_adep	= 'N'
			where	nr_sequencia		= nr_sequencia_w;

		elsif (nm_tabela_w	= 'PRESCR_DIETA') then

			update	prescr_dieta_hor
			set	nr_seq_jejum_susp	 = NULL,
				dt_suspensao		 = NULL,
				nm_usuario_susp		 = NULL
			where	nr_sequencia		= nr_sequencia_w;

		end if;

		CALL atualiza_ie_horario_susp(nr_prescr_item_w, nr_seq_item_w, nm_tabela_w);

		end;
	end loop;
	close C01;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
