CREATE OR REPLACE PROCEDURE desf_envio_prot_fornec ( nr_seq_protocolo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_lote_producao_w	bigint;
nr_seq_sttaus_atual_w	bigint;
dt_alteracao_w		timestamp;
nr_seq_status_novo_w	bigint;


C01 CURSOR FOR
	SELECT   DISTINCT b.nr_lote_producao
	FROM     lote_producao_hist_status b
	WHERE    b.nr_lote_producao IN (SELECT DISTINCT a.nr_lote_producao
	FROM     lote_producao_comp a
	WHERE    a.nr_seq_protocolo = nr_seq_protocolo_p)
	order by b.nr_lote_producao;


BEGIN

update  protocolo_fornec_opme
set     dt_retirada		 = NULL,
	cd_pessoa_retirada	 = NULL
where   nr_sequencia		=   nr_seq_protocolo_p;

commit;



open C01;
loop
fetch C01 into
	nr_lote_producao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

if (nr_lote_producao_w IS NOT NULL AND nr_lote_producao_w::text <> '') then

	select max(nr_seq_status)
	into STRICT   nr_seq_sttaus_atual_w
	from   lote_producao
	where  nr_lote_producao = nr_lote_producao_w;


	select 	 max(a.dt_alteracao),
		 max(a.nr_seq_status)
	into STRICT	 dt_alteracao_w,
		 nr_seq_status_novo_w
	from     lote_producao_hist_status a
	where    a.nr_sequencia = (SELECT max(b.nr_sequencia)
          		 	   from	  lote_producao_hist_status b
			 	   where  b.nr_lote_producao = nr_lote_producao_w
				   and    b.nr_seq_status <> nr_seq_sttaus_atual_w);



	update  lote_producao b
	set     b.nr_seq_status		=   nr_seq_status_novo_w
	where	nr_lote_producao	=   nr_lote_producao_w;

	commit;

end if;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
