SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desf_lib_prescr_conferencia ( nr_prescricao_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_prescr_lib_w	bigint;

BEGIN

	if (coalesce(nr_prescricao_p,0) > 0) then

		select 	max(nr_sequencia)
		into STRICT	nr_seq_prescr_lib_w
		from 	prescr_lib_conferencia
		where 	nr_prescricao = nr_prescricao_p;

		if (coalesce(nr_seq_prescr_lib_w,0) > 0) and (coalesce(ie_opcao_p,'XxX') = 'DLC') then -- Desfazer lib conferência
			insert into	prescr_lib_conf_hist(
						nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						nr_prescricao,
						ie_acao,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nm_usuario_conferencia,
						dt_lib_conferencia,
						nm_usuario_lib_conferencia,
						dt_inicio_conferencia,
						dt_fim_conferencia)
					SELECT	nextval('prescr_lib_conf_hist_seq'),
						clock_timestamp(),
						nm_usuario_p,
						nr_prescricao_p,
						'DLC',
						clock_timestamp(),
						nm_usuario_p,
						nm_usuario_conferencia,
						dt_lib_conferencia,
						nm_usuario_lib_conferencia,
						dt_inicio_conferencia,
						dt_fim_conferencia
					from	prescr_lib_conferencia
					where	nr_sequencia = nr_seq_prescr_lib_w;

			update	prescr_lib_conferencia
			set	dt_fim_conferencia  = NULL,
				dt_inicio_conferencia  = NULL,
				nm_usuario_conferencia  = NULL,
				dt_lib_conferencia  = NULL,
				nm_usuario_lib_conferencia  = NULL
			where	nr_sequencia = nr_seq_prescr_lib_w;

		elsif (coalesce(nr_seq_prescr_lib_w,0) > 0) and (coalesce(ie_opcao_p,'XxX') = 'DC') then -- Desfazer conferência
			insert into	prescr_lib_conf_hist(
						nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						nr_prescricao,
						ie_acao,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nm_usuario_conferencia,
						dt_lib_conferencia,
						nm_usuario_lib_conferencia,
						dt_inicio_conferencia,
						dt_fim_conferencia)
					SELECT	nextval('prescr_lib_conf_hist_seq'),
						clock_timestamp(),
						nm_usuario_p,
						nr_prescricao_p,
						'DC',
						clock_timestamp(),
						nm_usuario_p,
						nm_usuario_conferencia,
						dt_lib_conferencia,
						nm_usuario_lib_conferencia,
						dt_inicio_conferencia,
						dt_fim_conferencia
					from	prescr_lib_conferencia
					where	nr_sequencia = nr_seq_prescr_lib_w;

			update	prescr_lib_conferencia
			set	dt_fim_conferencia  = NULL,
				dt_inicio_conferencia  = NULL,
				nm_usuario_conferencia  = NULL,
				dt_lib_conferencia  = NULL,
				nm_usuario_lib_conferencia  = NULL
			where	nr_sequencia = nr_seq_prescr_lib_w;

		end if;
	end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
