CREATE OR REPLACE PROCEDURE destruir_caixa_prontuario ( nr_seq_caixa_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;
ds_parametro_w	varchar(10) := obter_valor_param_usuario(941,103,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_p);

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	same_prontuario
	where	nr_seq_caixa	=	nr_seq_caixa_p
	and	ie_status not in ('3','4','6','7','8')
	and	coalesce(nr_seq_agrup::text, '') = ''
	and	((ds_parametro_w = 'N') or (cd_estabelecimento = cd_estabelecimento_p));


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	update	same_prontuario
	set	ie_status    = '6'
	where	nr_sequencia = nr_sequencia_w;

	update	same_caixa
	set	dt_destruicao	=	clock_timestamp()
	where	nr_sequencia	=	nr_seq_caixa_p;

	insert into same_prontuario_hist(
		nr_sequencia,
		nr_seq_same,
		dt_atualizacao,
		nm_usuario,
		nr_seq_operacao,
		dt_historico,
		nr_seq_caixa,
		nr_seq_local,
		cd_pessoa_fisica)
	values (
		nextval('same_prontuario_hist_seq'),
		nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		15,
		clock_timestamp(),
		null,
		null,
		null);

	commit;

	end;
END LOOP;
CLOSE C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
