SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desvincular_adto_conta_pac ( nr_interno_conta_p bigint, nr_adiantamento_p bigint) AS $body$
DECLARE

 
qt_registros_w		bigint;					
 

BEGIN 
 
select	count(*) 
into STRICT	qt_registros_w 
from	titulo_receber_liq a, 
	titulo_receber t 
where	a.nr_adiantamento		= nr_adiantamento_p 
and	t.nr_interno_conta		= nr_interno_conta_p 
and	t.nr_titulo			= a.nr_titulo 
and	(a.dt_recebimento IS NOT NULL AND a.dt_recebimento::text <> '');
 
if (coalesce(qt_registros_w,0) > 0) then 
	/*Não é possível desvincular o adiantamento da conta paciente. O adiantamento está vinculado a uma baixa de título.*/
 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(239642);
end if;
 
qt_registros_w := 0;
 
select	count(*) 
into STRICT	qt_registros_w 
from	conta_paciente_adiant 
where	nr_interno_conta	= nr_interno_conta_p 
and	nr_adiantamento		= nr_adiantamento_p;
 
if (coalesce(qt_registros_w,0) > 0) then 
	delete	FROM conta_paciente_adiant 
	where	nr_interno_conta	= nr_interno_conta_p 
	and	nr_adiantamento		= nr_adiantamento_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE desvincular_adto_conta_pac ( nr_interno_conta_p bigint, nr_adiantamento_p bigint) FROM PUBLIC;

