SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desvincular_agenda_aut ( nr_sequencia_p bigint) AS $body$
DECLARE


nr_seq_agenda_p autorizacao_convenio.nr_seq_agenda%type;
ds_historico_w	varchar(4000):= '';


BEGIN

select 	coalesce(max(nr_seq_agenda),0)
into STRICT 	nr_seq_agenda_p
from 	autorizacao_convenio
where	nr_sequencia   = nr_sequencia_p;

if (nr_seq_agenda_p > 0)then
	update	agenda_paciente 
	set	ie_autorizacao  	 = NULL,
		nm_usuario	= wheb_usuario_pck.get_nm_usuario,
		dt_atualizacao  	= clock_timestamp()
	where	nr_sequencia    	= nr_seq_agenda_p;
	
	ds_historico_w := 'Campo: NR_SEQ_AGENDA - Antes: '||nr_seq_agenda_p||' Depois: NULL'||chr(13)||chr(10);
	CALL gravar_autor_conv_log_alter(nr_sequencia_p,Wheb_mensagem_pck.get_texto(1044707),substr(ds_historico_w,1,2000),wheb_usuario_pck.get_nm_usuario);
end if;

update	autorizacao_convenio
set	nr_seq_agenda  	 = NULL,
	nm_usuario	= wheb_usuario_pck.get_nm_usuario,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia   	= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
