SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desvincular_atendimento ( nr_sequencia_p bigint, ie_tipo_evento_p text) AS $body$
BEGIN
/*
ie_tipo_evento_p
DA - Desvinculação de atendimento;
VA - Vinculação de atendimento;

*/
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	if (ie_tipo_evento_p = 'DA')then
		update	agenda_consulta
		set	nr_atendimento			 = NULL,
			ie_status_agenda		= 'N',
			dt_vinculacao_atendimento	 = NULL,
			nm_usuario_vinculo_atend	 = NULL
		where	nr_sequencia	= nr_sequencia_p;
	elsif (ie_tipo_evento_p = 'VA')then
		update	agenda_consulta
		set	dt_vinculacao_atendimento	= clock_timestamp()
		where	nr_sequencia	= nr_sequencia_p;
	end if;

	commit;
	end;

end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
