CREATE OR REPLACE PROCEDURE desvincular_conv_receb_nf (nr_seq_receb_nf_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_vinculado_w		convenio_receb_titulo.vl_recebido%type;


BEGIN

SELECT (select coalesce(sum(x.vl_recebido),0) from convenio_receb_titulo x where x.nr_seq_nota_receb = r.nr_seq_nota_fiscal) vl_recebido
into STRICT	vl_vinculado_w
FROM	CONVENIO_RECEB_NF R
WHERE 	r.nr_sequencia = nr_seq_receb_nf_p;


if	vl_vinculado_w > 0 then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1135326);
end if;

delete
from	convenio_receb_nf
where	nr_sequencia = nr_seq_receb_nf_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
