CREATE OR REPLACE PROCEDURE desvincular_deposito_cheque (nr_seq_cheque_p bigint, nr_seq_deposito_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_cheque_w		double precision;
/* Projeto Multimoeda - Variáveis */

vl_cheque_estrang_w	double precision;
vl_deposito_estrang_w	double precision;
vl_cotacao_w		cotacao_moeda.vl_cotacao%type;


BEGIN

/* Projeto Multimoeda - Busca os dados do depósito para verificar se é moeda estrangeira.*/

select	max(vl_cheque_estrang),
	max(vl_cotacao)
into STRICT	vl_deposito_estrang_w,
	vl_cotacao_w
from	deposito
where	nr_sequencia = nr_seq_deposito_p;

delete	from deposito_cheque
where	nr_seq_cheque	= nr_seq_cheque_p
and	nr_seq_deposito	= nr_seq_deposito_p;

select	coalesce(sum(b.vl_cheque), 0),
	coalesce(sum(b.vl_cheque_estrang),0)
into STRICT	vl_cheque_w,
	vl_cheque_estrang_w
from	cheque_cr b,
	deposito_cheque a
where	a.nr_seq_cheque		= b.nr_seq_cheque
and	a.nr_seq_deposito	= nr_seq_deposito_p;

/* Projeto Multimoeda - Verifica se o depósito é moeda estrangeira, caso positivo atualiza os valores em moeda estrangeira,
		caso negativo mantém o processo atual.*/
if (coalesce(vl_deposito_estrang_w,0) <> 0 and coalesce(vl_cotacao_w,0) <> 0) then
	update	deposito
	set	nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		vl_cheque_estrang = vl_cheque_estrang_w,
		vl_cheque	= (vl_cheque_estrang_w * vl_cotacao),
		vl_complemento	= ((coalesce(vl_especie,0) - coalesce(vl_especie_estrang,0)) + ((vl_cheque_estrang_w * vl_cotacao) - vl_cheque_estrang_w))
	where	nr_sequencia	= nr_seq_deposito_p;
else
	update	deposito
	set	nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		vl_cheque	= vl_cheque_w
	where	nr_sequencia	= nr_seq_deposito_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
