SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desvincular_extrato_concil ( nr_seq_extrato_p bigint, nm_usuario_p text) AS $body$
DECLARE


ind		integer;
nr_seq_concil_w	bigint;

c01 CURSOR FOR
SELECT	distinct
	a.nr_seq_conciliacao
from	concil_banc_movto a,
	banco_extrato_lanc b
where	a.nr_sequencia		= b.nr_seq_concil
and	b.nr_seq_extrato	= nr_seq_extrato_p;

vet01	c01%Rowtype;

type reg_Concil is table of c01%RowType index by integer;

concil_w	reg_concil;

c02 CURSOR FOR
SELECT	y.nr_sequencia
from	concil_banc_movto y,
	banco_extrato_lanc x
where	y.nr_seq_conciliacao	= concil_w[ind].nr_seq_conciliacao
and	x.nr_seq_concil		= y.nr_sequencia
and	x.nr_seq_extrato	= nr_seq_extrato_p;


BEGIN

ind	:= 0;

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ind	:= ind + 1;
	concil_w[ind].nr_seq_conciliacao	:= vet01.nr_seq_conciliacao;
	end;
end loop;
close C01;

delete	from concil_banc_pend_bco a
where	a.nr_seq_lanc_extrato	in (SELECT	x.nr_sequencia
	from	banco_extrato_lanc x
	where	x.nr_seq_extrato	= nr_seq_extrato_p);

for ind in 1..concil_w.count loop
	begin

	open	c02;
	loop
	fetch	c02 into
		nr_seq_concil_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */

		update	banco_extrato_lanc a
		set	a.nr_seq_concil		 = NULL,
			a.ie_conciliacao	= 'N'
		where	a.nr_seq_concil		= nr_seq_concil_w;

		update	movto_trans_financ a
		set	a.nr_seq_concil		 = NULL,
			a.ie_conciliacao	= 'N',
			a.dt_atualizacao	= clock_timestamp(),
			a.nm_usuario		= nm_usuario_p
		where	a.nr_seq_concil		= nr_seq_concil_w;

		delete	from concil_banc_movto a
		where	nr_seq_conciliacao	= concil_w[ind].nr_seq_conciliacao
		and	a.nr_sequencia		= nr_seq_concil_w;

	end	loop;
	close	c02;

	CALL atualiza_dados_conciliacao(concil_w[ind].nr_seq_conciliacao, nm_usuario_p);

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
