CREATE OR REPLACE PROCEDURE desvincular_item_ordem_cotacao ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_cot_compra_w		cot_compra.nr_cot_compra%type;
nr_item_cot_compra_w	cot_compra_item.nr_item_cot_compra%type;
qt_registro_w		bigint;
cd_material_w		material.cd_material%type;


BEGIN

select	cd_material,
	nr_cot_compra,
	nr_item_cot_compra
into STRICT	cd_material_w,
	nr_cot_compra_w,
	nr_item_cot_compra_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;

update	ordem_compra_item
set	nr_cot_compra		 = NULL,
	nr_item_cot_compra	 = NULL,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_ordem_compra		= nr_ordem_compra_p
and	nr_item_oci		= nr_item_oci_p;

update	cot_compra_forn_item
set	nr_ordem_compra	 = NULL,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_cot_compra	= nr_cot_compra_w
and	nr_item_cot_compra = nr_item_cot_compra_w
and	nr_ordem_compra = nr_ordem_compra_p
and	cd_material = cd_material_w;

update	cot_compra
set	dt_geracao_ordem_compra	= '',
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_cot_compra		= nr_cot_compra_w;


CALL gerar_ordem_compra_hist(nr_ordem_compra_p,
			'S',
			wheb_mensagem_pck.get_Texto(311438), /*'Desvinculação do item da ordem de compra com cotação de compras',*/
			wheb_mensagem_pck.get_texto(311439,'NR_ITEM_OCI_P='|| NR_ITEM_OCI_P ||';NR_COT_COMPRA_W='|| NR_COT_COMPRA_W), /*'Item ' || nr_item_oci_p || ' foi vinculado da cotação de compra número ' || nr_cot_compra_w || ' através da opção Desvincular cotação de compras.',*/
			nm_usuario_p);

commit;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
