SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desvincular_item_reenv_escrit ( nr_seq_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lote_w				bigint;
vl_lote_w					double precision;


BEGIN

if (nr_seq_titulo_p IS NOT NULL AND nr_seq_titulo_p::text <> '') then
	select	max(nr_seq_lote)
	into STRICT	nr_seq_lote_w
	from	lote_cobr_esc_reenv_tit
	where	nr_sequencia	= nr_seq_titulo_p;

	delete from	lote_cobr_esc_reenv_tit
	where		nr_sequencia	= nr_seq_titulo_p;

	select	sum(vl_cobranca)
	into STRICT	vl_lote_w
	from	titulo_receber_cobr b,
		lote_cobr_esc_reenv_tit a
	where	a.nr_seq_tit_rec_cobr	= b.nr_sequencia
	and	a.nr_seq_lote		= nr_seq_lote_w;

	update	lote_cobr_escrit_reenvio
	set	vl_lote		= vl_lote_w
	where	nr_sequencia	= nr_seq_lote_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
