CREATE OR REPLACE PROCEDURE desvincular_nf_lote_protocolo (nr_seq_nf_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		bigint;
ds_observacao_w		varchar(255);
nr_seq_lote_prot_w	bigint;


BEGIN

select	nr_seq_lote_prot
into STRICT	nr_seq_lote_prot_w
from	nota_fiscal
where	nr_sequencia = nr_seq_nf_p;

ds_observacao_w	:= wheb_mensagem_pck.get_Texto(311444, 'NR_SEQ_LOTE_PROT_W='|| NR_SEQ_LOTE_PROT_W); /*'Desvinculado lote protocolo ' || to_char(nr_seq_lote_prot_w) || ' manualmente';*/
update	nota_fiscal
set	nr_seq_lote_prot  = NULL,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_sequencia = nr_seq_nf_p;

insert into nota_fiscal_hist(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_nota,
	cd_evento,
	ds_historico)
values (nextval('nota_fiscal_hist_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_nf_p,
	'56',
	ds_observacao_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
