CREATE OR REPLACE PROCEDURE desvincular_ordem_agrup ( nr_ordem_compra_p bigint, nr_ordem_agrup_p bigint, ie_permite_vincular_liberada_p text, ie_voltar_ordem_pend_p text, nm_usuario_p text) AS $body$
BEGIN
 
update	ordem_compra 
set	nr_ordem_agrup	 = NULL 
where	nr_ordem_compra = nr_ordem_agrup_p 
and	((ie_voltar_ordem_pend_p = 'S') or 
	((ie_voltar_ordem_pend_p = 'N') and (coalesce(dt_baixa::text, '') = ''))) 
and	((ie_permite_vincular_liberada_p = 'S') or 
	((ie_permite_vincular_liberada_p = 'N') and (coalesce(dt_aprovacao::text, '') = '') and (coalesce(dt_liberacao::text, '') = '')));
	 
if (ie_voltar_ordem_pend_p = 'S') then 
 
	delete	from ordem_compra_item 
	where	nr_ordem_compra = nr_ordem_compra_p 
	and	nr_ordem_agrup	= nr_ordem_agrup_p;
	 
	update	ordem_compra 
	set	dt_baixa  = NULL 
	where	nr_ordem_compra = nr_ordem_agrup_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
