CREATE OR REPLACE PROCEDURE desvincular_titulo_bordero_nc (nr_seq_bordero_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_titulo_w	bigint;


BEGIN 
 
select	max(a.nr_titulo) 
into STRICT	nr_titulo_w 
from	titulo_pagar a 
where	a.nr_seq_bordero_nc	= nr_seq_bordero_p;
 
CALL cancelar_titulo_pagar(nr_titulo_w, nm_usuario_p, clock_timestamp());
 
update	titulo_pagar 
set	nr_seq_bordero_nc	 = NULL, 
	ds_observacao_titulo	= 'Título cancelado pelo borderô de nota de crédito ' || nr_seq_bordero_p 
where	nr_titulo		= nr_titulo_w;
 
update	bordero_nc 
set	dt_geracao_titulo	 = NULL 
where	nr_sequencia		= nr_seq_bordero_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
