SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desvincular_titulo_conta ( nr_interno_conta_p bigint, nm_usuario_p text, ds_mensagem_retorno_p INOUT text) AS $body$
DECLARE

 
 
vl_saldo_titulo_w				titulo_receber.vl_saldo_titulo%type;
nr_titulo_w				titulo_receber.nr_titulo%type;
vl_titulo_w				titulo_receber.vl_titulo%type;
qt_titulo_w				bigint;
ie_situacao_w				titulo_receber.ie_situacao%type;
ie_desvincular_nf_titulo_w			varchar(1);
cd_estabelecimento_w			estabelecimento.cd_estabelecimento%type;
ie_forma_canc_tit_conta_w			parametro_contas_receber.ie_forma_canc_tit_conta%type;
nr_seq_titulo_w				titulo_receber_liq.nr_sequencia%type;
cd_moeda_w				titulo_receber.cd_moeda%type;
cd_tipo_recebimento_w			tipo_recebimento.cd_tipo_recebimento%type;
nr_seq_trans_fin_w				tipo_recebimento.nr_seq_trans_fin%type;
nr_titulo_contab_w				bigint := null;
ds_titulos_contab_w			varchar(4000);

 
 
c01 CURSOR FOR 
SELECT	nr_titulo, 
	ie_situacao, 
	cd_moeda, 
	vl_saldo_titulo 
from	titulo_receber 
where	nr_interno_conta 	= nr_interno_conta_p;


BEGIN 
 
select	count(*) 
into STRICT	qt_titulo_w 
from	titulo_receber 
where 	nr_interno_conta	= nr_interno_conta_p;
 
select	coalesce(sum(vl_saldo_titulo),0), 
	coalesce(sum(vl_titulo),0) 
into STRICT	vl_saldo_titulo_w, 
	vl_titulo_w 
from	titulo_receber 
where 	nr_interno_conta	= nr_interno_conta_p 
and	ie_situacao		<> '3';
 
select	max(a.cd_estabelecimento) 
into STRICT	cd_estabelecimento_w 
from	conta_paciente a 
where	a.nr_interno_conta	= nr_interno_conta_p;
 
Select	coalesce(max(ie_forma_canc_tit_conta), 'A') 
into STRICT	ie_forma_canc_tit_conta_w 
from	parametro_contas_receber 
where	cd_estabelecimento = cd_estabelecimento_w;
 
select	coalesce(min(cd_tipo_recebimento),4) 
into STRICT	cd_tipo_recebimento_w 
from	tipo_recebimento 
where	ie_tipo_consistencia = 4 
and   ((cd_estabelecimento = cd_estabelecimento_w) or (coalesce(cd_estabelecimento::text, '') = ''));
 
select	max(nr_seq_trans_fin) 
into STRICT	nr_seq_trans_fin_w 
from	tipo_recebimento 
where	cd_tipo_recebimento	= cd_tipo_recebimento_w;
 
if (qt_titulo_w = 0) and (vl_titulo_w = 0) then 
	ds_mensagem_retorno_p := Wheb_mensagem_pck.get_Texto(305997); /*'Não existem Títulos associados a esta conta';*/
elsif (vl_saldo_titulo_w = vl_titulo_w) then 
	begin 
	ds_titulos_contab_w	:= null;
	 
	open c01;
	loop 
	fetch c01 into 
		nr_titulo_w, 
		ie_situacao_w, 
		cd_moeda_w, 
		vl_saldo_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
 
		if (ie_situacao_w <> '3') then 
			if (ie_forma_canc_tit_conta_w = 'A') then 
				CALL Cancelar_Titulo_Receber(nr_titulo_w, nm_usuario_p, 'N',clock_timestamp());
			else 
 
				Select	coalesce(max(nr_sequencia),0) + 1 
				into STRICT	nr_seq_titulo_w 
				from	titulo_receber_liq 
				where	nr_titulo = nr_titulo_w;
 
				insert	into Titulo_Receber_liq(nr_titulo, 
					nr_sequencia, 		 
					dt_recebimento, 
					vl_recebido, 		 
					vl_descontos,			 
					vl_juros, 
					vl_multa, 		 
					cd_moeda, 			 
					dt_atualizacao, 
					nm_usuario,		 
					cd_tipo_recebimento, 	 
					ie_acao, 
					vl_rec_maior, 		 
					vl_glosa,		 
					ie_lib_caixa, 
					nr_seq_trans_fin, 
					nr_lote_contab_antecip, 
					nr_lote_contab_pro_rata, 
					nr_lote_contabil) 
				values (nr_titulo_w, 		 
					nr_seq_titulo_w, 		 
					clock_timestamp(), 
					vl_saldo_titulo_w,		 
					0,				 
					0, 
					0, 			 
					cd_moeda_w, 			 
					clock_timestamp(), 
					nm_usuario_p, 	 
					cd_tipo_recebimento_w,	 
					'I', 
					0, 			 
					0,				 
					'S', 
					nr_seq_trans_fin_w, 
					0, 
					0, 
					0);
 
				nr_titulo_contab_w	:= null;
				 
				CALL gerar_titulo_rec_liq_cc(cd_estabelecimento_w, null, nm_usuario_p, nr_titulo_w, nr_seq_titulo_w);
				 
				nr_titulo_contab_w := pls_gerar_tit_rec_liq_mens(nr_titulo_w, nr_seq_titulo_w, nm_usuario_p, nr_titulo_contab_w);
				 
				if (nr_titulo_contab_w IS NOT NULL AND nr_titulo_contab_w::text <> '') then 
					ds_titulos_contab_w	:= substr(ds_titulos_contab_w || nr_titulo_contab_w || ', ',1,4000);
				end if;
				 
				CALL atualizar_saldo_tit_rec(nr_titulo_w,nm_usuario_p);
 
				update	titulo_receber 
				set 	ie_situacao		= '3' 
				where	nr_titulo		= nr_titulo_w 
				and	vl_saldo_titulo		= 0 
				and	ie_situacao		in ('2', '4');
			end if;
		end if;
 
		update	titulo_receber 
		set	dt_liquidacao 		= clock_timestamp(), 
		  	nr_sequencia_doc 	= substr(nr_titulo, length(nr_titulo) - 5, 5), 
	  		nr_seq_protocolo 	 = NULL, 
		  	nr_interno_conta 	 = NULL, 
		  	ds_observacao_titulo 	= substr(WHEB_MENSAGEM_PCK.get_texto(306001, 
							'NR_INTERNO_CONTA_P='|| NR_INTERNO_CONTA_P || 
							';NM_USUARIO_P='|| NM_USUARIO_P || 
							';DT_CANCEL_W='|| clock_timestamp() || 
							';DS_OBSERVACAO_TITULO_W='||substr(ds_observacao_titulo,1,3800)),1,4000)							 
							/*Conta paciente = #@NR_INTERNO_CONTA_P#@ 
							Cancelado por #@NM_USUARIO_P#@ em #@DT_CANCEL_W#@ 
							#@DS_OBSERVACAO_TITULO_W#@*/
 
		where 	nr_interno_conta 	= nr_interno_conta_p;
		 
	end loop;
	close c01;
	 
	if (ds_titulos_contab_w IS NOT NULL AND ds_titulos_contab_w::text <> '') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(236517, 'NR_TITULO=' || ds_titulos_contab_w);
	end if;
 
	ie_desvincular_nf_titulo_w := obter_param_usuario(85, 139, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_desvincular_nf_titulo_w);
 
	if (coalesce(ie_desvincular_nf_titulo_w,'S') <> 'N') then 
 
		update	titulo_receber 
		set	nr_seq_nf_saida	 = NULL, 
			dt_atualizacao	= clock_timestamp(), 
			nm_usuario 	= nm_usuario_p 
		where	nr_seq_nf_saida	in (SELECT	nr_sequencia 
				from	nota_fiscal 
				where 	nr_interno_conta = nr_interno_conta_p);
 
	end if;
 
	ds_mensagem_retorno_p 		:= Wheb_mensagem_pck.get_Texto(306002); /*'Desvinculação Completada';*/
	end;
else 
	ds_mensagem_retorno_p := Wheb_mensagem_pck.get_Texto(306003); /*'O Saldo dos Títulos é diferente do valor Original dos mesmos';*/
end if;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE desvincular_titulo_conta ( nr_interno_conta_p bigint, nm_usuario_p text, ds_mensagem_retorno_p INOUT text) FROM PUBLIC;

