SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desvincular_titulo_lote_prot ( nr_seq_lote_protocolo_p bigint, nm_usuario_p text, ds_mensagem_retorno_p INOUT text) AS $body$
DECLARE

 
 
vl_saldo_titulo_w				double precision;
nr_titulo_w				bigint;
vl_titulo_w				double precision;
qt_titulo_w				bigint;
ie_situacao_w				varchar(10);
ie_desvincular_nf_titulo_w			varchar(1);
cd_estabelecimento_w			smallint;
ie_forma_canc_tit_prot_w			varchar(10);
nr_seq_titulo_w				bigint;
cd_moeda_w				bigint;
cd_tipo_recebimento_w			bigint;
nr_seq_trans_fin_w				bigint;
nr_titulo_contab_w				bigint;
ds_titulos_contab_w			varchar(4000);

c01 CURSOR FOR 
	SELECT	nr_titulo, 
		ie_situacao, 
		cd_moeda, 
		vl_saldo_titulo 
	from	titulo_receber 
	where	nr_seq_lote_prot 	= nr_seq_lote_protocolo_p;


BEGIN 
select	count(*) 
into STRICT	qt_titulo_w 
from	titulo_receber 
where 	nr_seq_lote_prot 	= nr_seq_lote_protocolo_p;
 
select	coalesce(sum(vl_saldo_titulo),0), 
	coalesce(sum(vl_titulo),0) 
into STRICT	vl_saldo_titulo_w, 
	vl_titulo_w 
from	titulo_receber 
where 	nr_seq_lote_prot	= nr_seq_lote_protocolo_p 
and	ie_situacao		<> '3';
 
select	max(a.cd_estabelecimento) 
into STRICT	cd_estabelecimento_w 
from	protocolo_convenio a 
where	a.nr_seq_lote_protocolo	= nr_seq_lote_protocolo_p;
 
Select	coalesce(max(ie_forma_canc_tit_prot), 'A') 
into STRICT	ie_forma_canc_tit_prot_w 
from	parametro_contas_receber 
where	cd_estabelecimento = cd_estabelecimento_w;
 
select	coalesce(min(cd_tipo_recebimento),4) 
into STRICT	cd_tipo_recebimento_w 
from	tipo_recebimento 
where	ie_tipo_consistencia = 4 
and   ((cd_estabelecimento = cd_estabelecimento_w) or (coalesce(cd_estabelecimento::text, '') = ''));
 
select	max(nr_seq_trans_fin) 
into STRICT	nr_seq_trans_fin_w 
from	tipo_recebimento 
where	cd_tipo_recebimento	= cd_tipo_recebimento_w;
 
if (qt_titulo_w = 0) and (vl_titulo_w = 0) then 
	ds_mensagem_retorno_p	:= wheb_mensagem_pck.get_texto(298039);
elsif (vl_saldo_titulo_w = vl_titulo_w) then 
	BEGIN 
	ds_titulos_contab_w	:= null;
	 
	open c01;
	loop 
	fetch c01 into 
		nr_titulo_w, 
		ie_situacao_w, 
		cd_moeda_w, 
		vl_saldo_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
			 
		if (ie_situacao_w <> '3') then 
			if (ie_forma_canc_tit_prot_w = 'A') then 
				CALL Cancelar_Titulo_Receber(nr_titulo_w, 
							nm_usuario_p, 
							'N', 
							clock_timestamp());
			else 
 
				Select	coalesce(max(nr_sequencia),0) + 1 
				into STRICT	nr_seq_titulo_w 
				from	titulo_receber_liq 
				where	nr_titulo	= nr_titulo_w;
 
				insert	into titulo_receber_liq(nr_titulo, 
					nr_sequencia, 		 
					dt_recebimento, 
					vl_recebido, 		 
					vl_descontos,			 
					vl_juros, 
					vl_multa, 		 
					cd_moeda, 			 
					dt_atualizacao, 
					nm_usuario,		 
					cd_tipo_recebimento, 	 
					ie_acao, 
					vl_rec_maior, 		 
					vl_glosa,		 
					ie_lib_caixa, 
					nr_seq_trans_fin, 
					nr_lote_contab_antecip, 
					nr_lote_contab_pro_rata, 
					nr_lote_contabil) 
				values (nr_titulo_w, 		 
					nr_seq_titulo_w, 		 
					clock_timestamp(), 
					vl_saldo_titulo_w,		 
					0,				 
					0, 
					0, 			 
					cd_moeda_w, 			 
					clock_timestamp(), 
					nm_usuario_p, 	 
					cd_tipo_recebimento_w,	 
					'I', 
					0, 			 
					0,				 
					'S', 
					nr_seq_trans_fin_w, 
					0, 
					0, 
					0);
 
				CALL gerar_titulo_rec_liq_cc(	cd_estabelecimento_w, 
								null, 
								nm_usuario_p, 
								nr_titulo_w, 
								nr_seq_titulo_w);
				 
				nr_titulo_contab_w	:= null;
				 
				nr_titulo_contab_w := pls_gerar_tit_rec_liq_mens(	nr_titulo_w, nr_seq_titulo_w, nm_usuario_p, nr_titulo_contab_w);
				 
				if (nr_titulo_contab_w IS NOT NULL AND nr_titulo_contab_w::text <> '') then 
					ds_titulos_contab_w	:= substr(ds_titulos_contab_w || nr_titulo_contab_w || ', ',1,4000);
				end if;
				 
				CALL atualizar_saldo_tit_rec(nr_titulo_w,nm_usuario_p);
 
				update	titulo_receber 
				set 	ie_situacao		= '3' 
				where	nr_titulo		= nr_titulo_w 
				and	vl_saldo_titulo		= 0 
				and	ie_situacao		in ('2', '4');
			end if;
		end if;
 
		update	titulo_receber 
		set	dt_liquidacao 		= clock_timestamp(), 
		  	nr_sequencia_doc 	= substr(nr_titulo, length(nr_titulo) - 5, 5), 
	  		nr_seq_lote_prot 	 = NULL, 
		  	ds_observacao_titulo 	= wheb_mensagem_pck.get_texto(298042, 
										'NR_SEQ_LOTE_PROT=' || nr_seq_lote_protocolo_p || ';' || 
										'NM_CANC=' || nm_usuario_p || ';' || 
										'DT_CANC=' || clock_timestamp() || ';' || 
										'DS_OBS_TIT=' || ds_observacao_titulo) 
		where 	nr_seq_lote_prot 	= nr_seq_lote_protocolo_p;
 
	end loop;
	close c01;
	 
	if (ds_titulos_contab_w IS NOT NULL AND ds_titulos_contab_w::text <> '') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(236517, 'NR_TITULO=' || ds_titulos_contab_w);
	end if;
 
	ie_desvincular_nf_titulo_w := obter_param_usuario(85, 139, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_desvincular_nf_titulo_w);
 
	if (coalesce(ie_desvincular_nf_titulo_w,'S') <> 'N') then 
 
		update	titulo_receber 
		set	nr_seq_nf_saida	 = NULL, 
			dt_atualizacao	= clock_timestamp(), 
			nm_usuario 	= nm_usuario_p 
		where	nr_seq_nf_saida	in (SELECT	nr_sequencia 
						from	nota_fiscal 
						where 	nr_seq_lote_prot	= nr_seq_lote_protocolo_p);
	end if;
 
	update 	protocolo_convenio 
	set 	dt_integracao_cr 	 = NULL 
	where 	nr_seq_lote_protocolo 	= nr_seq_lote_protocolo_p;
 
	ds_mensagem_retorno_p	:= wheb_mensagem_pck.get_texto(298051);
	end;
else 
	ds_mensagem_retorno_p	:= wheb_mensagem_pck.get_texto(298052);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE desvincular_titulo_lote_prot ( nr_seq_lote_protocolo_p bigint, nm_usuario_p text, ds_mensagem_retorno_p INOUT text) FROM PUBLIC;

