SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desvincular_titulo_pagar_nc (nr_seq_nota_credito_p bigint, nr_titulo_p bigint, ie_todos_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_titulo_w	bigint;

c01 CURSOR FOR 
SELECT	a.nr_titulo 
from	titulo_pagar a 
where	a.nr_seq_nota_credito	= nr_seq_nota_credito_p 
and	a.ie_situacao		<> 'C' 
and ('S' = ie_todos_p or a.nr_titulo = nr_titulo_p);


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	update	titulo_pagar 
	set	nr_seq_nota_credito	 = NULL, 
		ds_observacao_titulo	= WHEB_MENSAGEM_PCK.get_texto(457571, 'nr_seq_nota_credito='||nr_seq_nota_credito_p) 
	where	nr_titulo		= nr_titulo_w;
 
	CALL cancelar_titulo_pagar(nr_titulo_w, nm_usuario_p, clock_timestamp());
 
end loop;
close c01;
 
CALL ATUALIZAR_SALDO_NOTA_CREDITO(nr_seq_nota_credito_p, nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
