SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desvincular_titulo_receber_nf ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_titulo_w		bigint;
ds_observacao_w		varchar(4000);

c01 CURSOR FOR
SELECT	nr_titulo
from	titulo_receber
where	nr_seq_nf_saida = nr_sequencia_p;


BEGIN
open c01;
loop
fetch c01 into
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	update	titulo_receber
	set	nr_documento		 = NULL,
		nr_seq_nf_saida		 = NULL,
		nr_nota_fiscal		= '',
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_titulo			= nr_titulo_w;

	select	ds_observacao
	into STRICT	ds_observacao_w
	from	nota_fiscal
	where	nr_sequencia = nr_sequencia_p;


	ds_observacao_w   := SUBSTR(wheb_mensagem_pck.get_Texto(311555, 'NR_TITULO_W='|| NR_TITULO_W||';DS_OBSERVACAO_W='||ds_observacao_w),1,4000);
				/*substr('Nota Desvinculada do titulo a receber ' || '(' || nr_titulo_w || ')' || chr(10) || ds_observacao_w,1,4000);*/

	update	nota_fiscal
	set	ds_observacao = ds_observacao_w,
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;

	CALL gerar_historico_nota_fiscal(nr_sequencia_p, nm_usuario_p, '49', wheb_mensagem_pck.get_Texto(311556, 'NR_TITULO_W='|| NR_TITULO_W));
									/*Nota desvinculada do t√≠tulo = #@NR_TITULO_W#@*/

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
