SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desvincular_titulo_rec_bordero (nr_bordero_p bigint, nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_receb_w		bordero_tit_rec.nr_seq_receb%type;


BEGIN

    select max(nr_seq_receb)
    into STRICT nr_seq_receb_w
    from bordero_tit_rec
    where	nr_bordero	= nr_bordero_p
    and	nr_titulo	= nr_titulo_p;

    if (nr_seq_receb_w IS NOT NULL AND nr_seq_receb_w::text <> '') then
        delete from convenio_receb_titulo 
        where nr_titulo = nr_titulo_p
        and nr_bordero = nr_bordero_p
        and nr_seq_receb = nr_seq_receb_w;
    end if;

    delete	from bordero_tit_rec
    where	nr_bordero	= nr_bordero_p
    and	nr_titulo	= nr_titulo_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
