SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desvincula_atend_carta_eme ( nr_seq_lancamento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_atendimento_w		bigint;
nr_prescricao_w		bigint;
qt_existe_w		bigint;


BEGIN 
 
select	coalesce(max(nr_atendimento),0), 
	coalesce(max(nr_prescricao),0) 
into STRICT	nr_atendimento_w, 
	nr_prescricao_w 
from	sup_lanc_cartao 
where	nr_sequencia = nr_seq_lancamento_p;
 
if (nr_atendimento_w = 0) then 
	--'Não há atendimento vinculado ao cartão!' 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(233253);
elsif (nr_prescricao_w = 0) then 
	--'Não há prescrição vinculada ao cartão!' 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(233254);
end if;
 
select	count(*) 
into STRICT	qt_existe_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_w 
and	nr_atendimento = nr_atendimento_w 
and ((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or 
	(dt_liberacao_farmacia IS NOT NULL AND dt_liberacao_farmacia::text <> '') or 
	(dt_liberacao_aux IS NOT NULL AND dt_liberacao_aux::text <> '') or 
	(dt_liberacao_medico IS NOT NULL AND dt_liberacao_medico::text <> ''));
 
if (qt_existe_w > 0) then 
	--'A prescrição vinculada a este cartão já está liberada!' 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(233255);
end if;
 
delete 
from	material_atend_paciente 
where	nr_atendimento = nr_atendimento_w 
and	nr_prescricao = nr_prescricao_w;
 
CALL Suspender_Prescricao(nr_prescricao_w,0,'',nm_usuario_p, 'N');
 
update	sup_lanc_cartao 
set	nr_atendimento  = NULL, 
	nr_prescricao  = NULL, 
	dt_atualizacao = clock_timestamp(), 
	nm_usuario = nm_usuario_p 
where	nr_sequencia = nr_seq_lancamento_p;
 
update 	sup_lanc_cartao_item 
set 	ie_conta = 'N' 
where	nr_seq_lancamento = nr_seq_lancamento_p;
 
--gravar_log__tasy(44002,'Desvinculado o atendimento do lançamento ' || nr_seq_lancamento_p || '. Atend./Prescr. anterior: ' || nr_atendimento_w || '/' || nr_prescricao_w,nm_usuario_p); 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE desvincula_atend_carta_eme ( nr_seq_lancamento_p bigint, nm_usuario_p text) FROM PUBLIC;

