SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desvincula_cirurgia_agenda ( nr_sequencia_p bigint, ie_opcao_p text, nr_cirurgia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint ) AS $body$
DECLARE


/*
ie_opcao_p
C - Cirurgia
P - Prescrição
*/
ie_atualiza_data_w     varchar(1);
nr_cirurgia_w	       bigint;
dt_prevista_w          timestamp;


BEGIN
ie_atualiza_data_w := Obter_Param_Usuario(871, 563, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_atualiza_data_w);

if (ie_opcao_p = 'C') then
	if (coalesce(nr_cirurgia_p,0) = 0) then
		select	max(nr_cirurgia)
		into STRICT	nr_cirurgia_w
		from	agenda_paciente
		where	nr_sequencia	=	nr_sequencia_p;

		if (nr_cirurgia_w > 0) then
			update	cirurgia
			set	nr_seq_agenda	 = NULL
			where	nr_cirurgia	=	nr_cirurgia_w;
		end if;

		update	prescr_medica
		set	nr_seq_agenda 	 = NULL
		where	nr_seq_agenda 	= nr_sequencia_p;
	end if;

	if (ie_atualiza_data_w = 'S') then

		if (coalesce(nr_sequencia_p,0) > 0) then
			select  max(hr_inicio)
			into STRICT	dt_prevista_w
			from	agenda_paciente
			where	nr_sequencia	=	nr_sequencia_p;
		end if;


		if (coalesce(nr_cirurgia_p,0) > 0) then
			update cirurgia
			set dt_inicio_prevista  =     dt_prevista_w
			where nr_cirurgia	   =	nr_cirurgia_p;
		end if;
	end if;

	update	agenda_paciente
	set	nr_cirurgia 	= CASE WHEN coalesce(nr_cirurgia_p,0)=0 THEN null  ELSE nr_cirurgia_p END
	where	nr_sequencia 	= nr_sequencia_p;



elsif (ie_opcao_p = 'P') then
	update	prescr_medica
	set	nr_seq_agenda 	 = NULL
	where	nr_seq_agenda 	= nr_sequencia_p;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
