CREATE OR REPLACE PROCEDURE desvincula_conta_pend (nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_sequencia_w	bigint;


C01 CURSOR FOR
	SELECT	nr_sequencia
	from	cta_pendencia
	where	nr_interno_conta = nr_interno_conta_p;



BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	begin
	update	cta_pendencia
	set	nr_seq_conta_orig = nr_interno_conta_p,
		nr_interno_conta  = NULL,
		dt_desvinculacao = clock_timestamp(),
		nm_usuario_desvinc = nm_usuario_p,
		DS_COMPLEMENTO = substr(wheb_mensagem_pck.get_texto(279844, 'DS_COMPLEMENTO_P=' || DS_COMPLEMENTO || ';NR_INTERNO_CONTA_P=' || nr_interno_conta_p),1,255)
	where	nr_sequencia = nr_sequencia_w;
	exception
	when others then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(279843);
	end;

	end;
end loop;
close C01;

commit;

end;
$body$
