CREATE OR REPLACE PROCEDURE desvinc_ordem_compra_contrato ( nr_seq_ordem_compra_p bigint, nr_seq_contrato_p bigint, nm_usuario_p text) AS $body$
BEGIN
						 
update	ordem_compra 
set 	nr_seq_contrato  = NULL, 
	nm_usuario	= nm_usuario_p, 
	dt_atualizacao	= clock_timestamp() 
where 	nr_ordem_compra = nr_Seq_ordem_compra_p;
 
 
update	ordem_compra_item 
set	nr_contrato	 = NULL, 
	nm_usuario	= nm_usuario_p, 
	dt_atualizacao	= clock_timestamp() 
where	nr_ordem_compra	= nr_Seq_ordem_compra_p 
and	nr_contrato	= nr_seq_contrato_p;
 
commit;
 
end	;
$body$
