SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE des_atualizar_meta_dia (dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w		bigint;
nr_seq_grupo_des_w	bigint;
dt_inicial_w		timestamp;
dt_final_w		timestamp;
qt_ordem_atual_w	bigint;
		
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		nr_seq_grupo_des 
	from	desenvolvimento_meta_OS 
	where	dt_meta between dt_inicial_w and dt_final_w 
	and	coalesce(qt_meta_real::text, '') = '' 
	order by nr_sequencia;
		
		 

BEGIN 
 
dt_inicial_w		:= trunc(dt_referencia_p, 'dd');
dt_final_w		:= trunc(dt_referencia_p, 'dd') + 86399/86400;
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w, 
	nr_seq_grupo_des_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	count(*) qt_ordem 
	into STRICT	qt_ordem_atual_w 
	from	man_ordem_servico_v x, 
		man_ordem_servico a 
	where	a.nr_sequencia = x.nr_sequencia 
	and  	coalesce(a.nr_seq_meta_pe::text, '') = '' 
	and   coalesce(a.nr_seq_obj_bsc::text, '') = '' 
	and   a.nr_seq_grupo_des = nr_seq_grupo_des_w 
	and   a.nr_seq_estagio in (4,731,732,741) 
	and	substr(obter_se_ordem_vinc_projeto(a.nr_sequencia,'2'),1,1) = 'N' 
	and   a.ie_status_ordem in ('1','2');	
	 
	update	desenvolvimento_meta_OS 
	set	qt_meta_real	= qt_ordem_atual_w 
	where	nr_sequencia	= nr_sequencia_w;
	 
	end;
end loop;
close C01;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
