SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE des_encaminha_os_fin_desenv ( nr_sequencia_p bigint, nm_usuario_p text, ie_envia_orc_p INOUT text) AS $body$
DECLARE


nr_seq_orc_w		bigint;
ds_relat_tecnico_w	varchar(4000);
nm_usuario_gestor_w	varchar(15);
qt_existe_w		bigint;
nr_seq_cliente_w	bigint;
ie_gera_novo_orc_w	varchar(1)	:= 'N';


BEGIN

ie_envia_orc_p	:= 'N';

CALL inserir_analista_exec(nr_sequencia_p,nm_usuario_p);

update	man_ordem_servico_exec
set	dt_fim_execucao = clock_timestamp(),
	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
where	nr_seq_ordem = nr_sequencia_p
and	nm_usuario_exec in (
		SELECT	x.nm_usuario
		from	usuario x
		where	x.cd_setor_atendimento = 1);

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_orc_w
from	man_ordem_servico_orc
where	nr_seq_ordem = nr_sequencia_p;

if (nr_seq_orc_w <> 0) then
	begin

	update	man_ordem_servico_orc a
	set	dt_validade = trunc(clock_timestamp() + interval '30 days'),
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	a.nr_sequencia in (
			SELECT	max(x.nr_sequencia)
			from	man_ordem_servico_orc x
			where	x.nr_seq_ordem = a.nr_seq_ordem
			group by x.ie_tipo_orcamento);

	select	nr_seq_cliente
	into STRICT	nr_seq_cliente_w
	from	man_ordem_servico
	where	nr_sequencia = nr_sequencia_p;

	/*Busca o gestor do cliente no pós vendas*/

	begin
	select	coalesce(substr(obter_usuario_pf(cd_pessoa_fisica),1,15),'Enedir')
	into STRICT	nm_usuario_gestor_w
	from	com_cliente_conta
	where	nr_seq_cliente = nr_seq_cliente_w
	and	clock_timestamp() between coalesce(dt_inicial,clock_timestamp()) and coalesce(dt_final,clock_timestamp())  LIMIT 1;
	exception
	when others then
		nm_usuario_gestor_w := 'Enedir';
	end;

	/*Verifica se o mesmo já existe como executor, senão, cria*/

	select	count(*)
	into STRICT	qt_existe_w
	from	man_ordem_servico_exec
	where	nr_seq_ordem 	= nr_sequencia_p
	and	nm_usuario_exec 	= nm_usuario_gestor_w;

	if (qt_existe_w = 0) then
		insert into man_ordem_servico_exec(
				nr_sequencia,
				nr_seq_ordem,
				dt_atualizacao,
				nm_usuario,
				nm_usuario_exec,
				qt_min_prev,
				nr_seq_tipo_exec,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
			values (	nextval('man_ordem_servico_exec_seq'),
				nr_sequencia_p,
				clock_timestamp(),
				nm_usuario_p,
				nm_usuario_gestor_w,
				45,
				'3',
				clock_timestamp(),
				nm_usuario_p);
	end if;

	begin
	select	a.ds_relat_tecnico
	into STRICT	ds_relat_tecnico_w
	from	man_ordem_serv_tecnico a
	where	a.nr_seq_ordem_serv = nr_sequencia_p
	and	a.nr_sequencia = (
			SELECT	max(x.nr_sequencia)
			from	man_ordem_serv_tecnico x
			where	x.nr_seq_ordem_serv = a.nr_seq_ordem_serv
			and	x.nr_seq_tipo = 34);

	insert into man_ordem_serv_tecnico(nr_sequencia,
			nr_seq_ordem_serv,
			dt_atualizacao,
			nm_usuario,
			ds_relat_tecnico,
			cd_versao_tasy,
			dt_historico,
			ie_origem,
			nr_seq_tipo,
			dt_liberacao,
			nm_usuario_lib,
			ie_grau_satisfacao,
			dt_envio,
			ie_relevante_teste,
			nr_seq_motivo_desvio_prev)
		values (nextval('man_ordem_serv_tecnico_seq'),
			nr_sequencia_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_relat_tecnico_w,
			'',
			clock_timestamp(),
			'I',
			1,
			clock_timestamp(),
			nm_usuario_p,
			'',
			'',
			'N',
			'');
	exception when others then
		ie_gera_novo_orc_w	:= 'S';
	end;

	end;
end if;

if (ie_gera_novo_orc_w = 'S') then

	insert into man_ordem_serv_tecnico(nr_sequencia,
			nr_seq_ordem_serv,
			dt_atualizacao,
			nm_usuario,
			ds_relat_tecnico,
			cd_versao_tasy,
			dt_historico,
			ie_origem,
			nr_seq_tipo,
			dt_liberacao,
			nm_usuario_lib,
			ie_grau_satisfacao,
			dt_envio,
			ie_relevante_teste,
			nr_seq_motivo_desvio_prev)
		values (nextval('man_ordem_serv_tecnico_seq'),
			nr_sequencia_p,
			clock_timestamp(),
			nm_usuario_p,
			'Favor, definir a complexidade e gerar o orçamento novamente',
			'',
			clock_timestamp(),
			'I',
			7,
			clock_timestamp(),
			nm_usuario_p,
			'',
			'',
			'N',
			'');

	nr_seq_orc_w	:= 0;

end if;

/* Alterado estágio para Desenv antes de aguardando info */

/* para que quando a OS voltar, pare no desenvolvimento */

update	man_ordem_servico
set	nr_seq_estagio = 4,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_sequencia = nr_sequencia_p;

commit;

if (nr_seq_orc_w <> 0) then

	update	man_ordem_servico
	set	nr_seq_estagio = 41,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;

	commit;

	ie_envia_orc_p	:= 'S';

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE des_encaminha_os_fin_desenv ( nr_sequencia_p bigint, nm_usuario_p text, ie_envia_orc_p INOUT text) FROM PUBLIC;

