CREATE OR REPLACE PROCEDURE des_liberar_historico_projeto (nr_seq_historico_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_hist_w		bigint;
nr_seq_ordem_serv_w	bigint;
ie_gravar_ordem_serv_w	varchar(01);
ds_historico_w		text;
		

BEGIN

select	a.ds_historico,
	b.nr_seq_ordem_serv,
	c.ie_gravar_ordem_serv
into STRICT	ds_historico_w,
	nr_seq_ordem_serv_w,
	ie_gravar_ordem_serv_w
from	com_tipo_historico c,
	com_cliente_hist a, 
	proj_projeto b
where	a.nr_sequencia		= nr_seq_historico_p
and	a.nr_seq_tipo		= c.nr_sequencia
and	a.nr_seq_projeto	= b.nr_sequencia;

update	com_cliente_hist
set	dt_liberacao		= clock_timestamp(),
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_historico_p;

if (ie_gravar_ordem_serv_w = 'S') and (nr_seq_ordem_serv_w IS NOT NULL AND nr_seq_ordem_serv_w::text <> '') then
	begin
	select	nextval('man_ordem_serv_tecnico_seq')
	into STRICT	nr_seq_hist_w
	;
	
	Insert into man_ordem_serv_tecnico(nr_sequencia,
		nr_seq_ordem_serv,      
		dt_atualizacao,         
		nm_usuario,             
		ds_relat_tecnico,       
		cd_versao_tasy,         
		dt_historico ,          
		ie_origem,              
		nr_seq_tipo,            
		nm_usuario_lib ,        
		dt_liberacao ,          
		ie_grau_satisfacao)
	values (nr_seq_hist_w,
		nr_seq_ordem_serv_w,
		clock_timestamp(),
		nm_usuario_p,
		ds_historico_w,
		null,
		clock_timestamp(),
		'E',
		1, nm_usuario_p, clock_timestamp(), null);	
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
