SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE des_liberar_req_item_versao ( nr_seq_versao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gravar data de liberação na versão atual e inativar demais versões anteriores do requisito do projeto
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_req_item_w	bigint;


BEGIN

if (nr_seq_versao_p IS NOT NULL AND nr_seq_versao_p::text <> '') then
	select	a.nr_seq_req_item
	into STRICT	nr_seq_req_item_w
	from	des_requisito_item_versao a
	where	a.nr_sequencia	= nr_seq_versao_p;

	update	des_requisito_item_versao a
	set	dt_liberacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_versao_p;

	update	des_requisito_item_versao a
	set	a.dt_inativacao	= clock_timestamp(),
		a.nm_usuario	= nm_usuario_p,
		a.dt_atualizacao = clock_timestamp()
	where	nr_seq_req_item = nr_seq_req_item_w
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(dt_inativacao::text, '') = ''
	and	nr_sequencia	<> nr_seq_versao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
