CREATE OR REPLACE PROCEDURE des_proj_gerar_escore_tempo ( nr_seq_item_p bigint, nr_seq_proj_p bigint, qt_escore_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_ponto_w			double precision;
qt_peso_w			bigint;
qt_escore_real_w			bigint;
pr_escore_real_w			bigint;


qt_real_w			double precision;
qt_prev_w			double precision;
qt_perc_real_w			double precision;


BEGIN

qt_escore_real_w	:= qt_escore_p;
pr_escore_real_w	:= 100;


select 	coalesce(max(QT_HORAS_REALIZADO),0),
	coalesce(max(QT_TOTAL_HORAS),0)
into STRICT	qt_real_w,
	qt_prev_w
from 	proj_cronograma
where 	nr_seq_proj = nr_seq_proj_p
and 	IE_SITUACAO = 'A'
and 	IE_TIPO_CRONOGRAMA = 'E';

if (qt_real_w > 0) and (qt_prev_w > 0)   then
	begin
	select	((qt_real_w * 100)/qt_prev_w)
	into STRICT	qt_perc_real_w
	;

	if (qt_perc_real_w >= 90 and qt_perc_real_w <= 110) then
		qt_ponto_w	:= 1;
	elsif (qt_perc_real_w >= 80 and qt_perc_real_w <= 120) then
		qt_ponto_w	:= 0.9;
	elsif (qt_perc_real_w >= 70 and qt_perc_real_w <= 130) then
		qt_ponto_w	:= 0.8;
	elsif (qt_perc_real_w >= 60 and qt_perc_real_w <= 140) then
		qt_ponto_w	:= 0.7;
	elsif (qt_perc_real_w >= 50 and qt_perc_real_w <= 150) then
		qt_ponto_w	:= 0.6;
	else
		qt_ponto_w	:= 0;
	end if;

	qt_escore_real_w	:= qt_escore_p * qt_ponto_w;
	pr_escore_real_w	:= dividir(qt_escore_real_w * 100, qt_escore_p);

	end;
end if;

update	proj_escore_item
set	qt_nota_maxima	= qt_peso_w,
	qt_nota		= qt_ponto_w,
	qt_escore_real	= qt_escore_real_w,
	pr_escore_real	= pr_escore_real_w
where	nr_sequencia	= nr_seq_item_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
