CREATE OR REPLACE PROCEDURE devolucao_exame ( nr_prontuario_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_emprestimo_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_emprestimo_w
from	exame_emprestimo
where	nr_prontuario	= nr_prontuario_p
and	coalesce(dt_devolucao::text, '') = '';

if (qt_emprestimo_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(282431);
end if;

update	exame_emprestimo
set	dt_devolucao	= clock_timestamp(),
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p,
	nm_usuario_dev	= nm_usuario_p
where	nr_prontuario	= nr_prontuario_p
and	coalesce(dt_devolucao::text, '') = '';

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
